import itertools

def solution1(number, k):
    tt = [''.join(list(x)) for x in itertools.combinations(number, len(number) - k)]
    return max(tt);

def solution2(number, k):
    number = list(number)
    length = len(number) - k
    answer = number[-length:]
    
    for x in number[-length-1::-1]:
        if x >= answer[0]:
            changeIdx = 0
            for idx in range(len(answer)-1):
                if answer[idx] < answer[idx+1]:
                    changeIdx = idx
                    break
            answer = [x] + answer[0:changeIdx] + answer[changeIdx+1:]
                    
    return ''.join(answer)

def solution3(number, k):
    number = list(number)
    length = len(number) - k
    answer = number[-length:]
    
    for x in number[-length-1::-1]:
        if x >= answer[0]:
            newNum = x
            lastIdx=0
            for idx in range(len(answer)):
                lastIdx = idx
                if answer[idx] < newNum:
                    temp = answer[idx]
                    answer[idx] = newNum
                    newNum = temp
                elif answer[idx] == newNum:
                    continue
                else:
                    break
            if idx == length and x == 9: break
            
    return ''.join(answer)

def solution(number, k):
    number = list(number)
    length = len(number) - k
    answer = number[-length:]
    answerPos = []
    
    answer.append('9')
    for idx in range(len(answer)-1):
        if answer[idx] < answer[idx+1]:
            answerPos.append(idx)
    
    for x in number[-length-1::-1]:
        if len(answerPos) < 1: break
        if x >= answer[0]:
            pos=answerPos[0]
            for idx in range(pos, 0, -1):
                answer[idx] = answer[idx-1]
            answer[0] = x
            if answer[pos] >= answer[pos+1]:
                answerPos.pop(0)
            
    return ''.join(answer[:-1])
    
